mapper.insert:
INSERT INTO MI_FILE( FILE_IDX
				   , BOARD_IDX
				   , ORI_FILE_NAME
				   , STORED_FILE_NAME
				   , DEL_YN ) 
			  VALUES((SELECT NVL(MAX(FILE_IDX),0)+1 FROM MI_FILE)
			       , #{board_idx}
			       , #{ori_file_name}
			       , #{stored_file_name}
			       , 'N')
